memberdef.initializer:
do {                                                                                               \
    if (var##rule == (SetRule)ApplyNow || (withdrawn && var##rule == (SetRule)ForceTemporarily)) { \
        var##rule = UnusedSetRule;                                                                 \
        changed = true;                                                                            \
    }                                                                                              \
} while (false)

do {                                                             \
    if (withdrawn && var##rule == (ForceRule)ForceTemporarily) { \
        var##rule = UnusedForceRule;                             \
        changed = true;                                          \
    }                                                            \
} while (false)
